import React, { useState } from 'react';
import ModelSTL from './ModelSTL';
import './css/ModelView.css';

const ModelView = () => {
    const [url, setUrl] = useState(null);
    const normalMapUrl = "textura.png"; // Укажите путь к нормальной карте

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const url = URL.createObjectURL(file);
            setUrl(url);
        }
    };

    return (
        <div className="model-view">
            <h1>3D Model Viewer</h1>
            <div className="file-upload">
                <label htmlFor="file-input">
                    <img src="upload-icon.png" alt="Upload Icon" className="upload-icon" />
                </label>
                <input id="file-input" type="file" accept=".stl" onChange={handleFileChange} />
            </div>
            {url && <ModelSTL url={url} normalMapUrl={normalMapUrl} />}
        </div>
    );
};

export default ModelView;
