import React, { useRef, useState, useEffect } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { STLLoader } from 'three/examples/jsm/loaders/STLLoader';
import * as THREE from 'three';
import { EffectComposer, Bloom } from '@react-three/postprocessing';

const STLModel = ({ url }) => {
    const ref = useRef();
    const { camera } = useThree();
    const [geometry, setGeometry] = useState(null);

    useEffect(() => {
        const loader = new STLLoader();
        loader.load(url, (geometry) => {
            geometry.center(); // Центрируем модель
            setGeometry(geometry);

            // Настройка камеры
            const box = new THREE.Box3().setFromObject(new THREE.Mesh(geometry));
            const size = box.getSize(new THREE.Vector3());
            const maxDim = Math.max(size.x, size.y, size.z);
            const fov = camera.fov * (Math.PI / 180);
            let cameraZ = Math.abs(maxDim / 2 / Math.tan(fov / 2)); // Умножение на два увеличивает значение Z
            cameraZ *= 1.5; // Увеличиваем значение Z для лучшего обзора

            camera.position.set(0, 0, cameraZ);
            camera.lookAt(0, 0, 0);
        });
    }, [url, camera]);

    useFrame((state, delta) => {
        if (ref.current) {
            ref.current.rotation.x += delta * 0.1;
            ref.current.rotation.y += delta * 0.1;
        }
    });

    if (!geometry) return null;

    return (
      <mesh geometry={geometry} ref={ref} scale={1} castShadow receiveShadow>
            <meshStandardMaterial
                color={'#888888'} // Нейтральный серый цвет
                metalness={0.8} // Более высокое значение для блеска
                roughness={0.2} // Более низкое значение для гладкой поверхности
                emissive={'#888888'} // Желтый цвет для контраста
                emissiveIntensity={1} // Более сильное свечение
            />
        </mesh>
    );
};

const ModelSTL = ({ url }) => {
    return (
      <Canvas shadows>
            <ambientLight intensity={0.5} />
            <directionalLight position={[5, 10, 7.5]} intensity={0.8} castShadow />
            <pointLight position={[-10, -10, -10]} intensity={0.5} />
            <spotLight position={[10, 10, 10]} angle={0.3} penumbra={1} intensity={1} castShadow />
            <STLModel url={url} />
            <OrbitControls />
            <EffectComposer>
                <Bloom luminanceThreshold={0} luminanceSmoothing={0.9} intensity={1.5} />
            </EffectComposer>
        </Canvas>
    );
};

export default ModelSTL;
