import React, { useEffect } from 'react';
import * as OV from 'online-3d-viewer';

const View3d = () => {
    useEffect(() => {
        OV.SetExternalLibLocation('../libs');

        let parentDiv = document.getElementById('viewer');
        if (!parentDiv) {
            console.error('Element with ID "viewer" not found.');
            return;
        }

        let viewer = new OV.EmbeddedViewer(parentDiv, {
            camera: new OV.Camera(
                new OV.Coord3D(-1.5, 2.0, 3.0),
                new OV.Coord3D(0.0, 0.0, 0.0),
                new OV.Coord3D(0.0, 1.0, 0.0),
                45.0
            ),
            backgroundColor: new OV.RGBAColor(255, 255, 255, 255),
            defaultColor: new OV.RGBColor(200, 200, 200),
            edgeSettings: new OV.EdgeSettings(false, new OV.RGBColor(0, 0, 0), 1),
            environmentSettings: new OV.EnvironmentSettings([
                '../envmaps/fishermans_bastion/posx.jpg',
                '../envmaps/fishermans_bastion/negx.jpg',
                '../envmaps/fishermans_bastion/posy.jpg',
                '../envmaps/fishermans_bastion/negy.jpg',
                '../envmaps/fishermans_bastion/posz.jpg',
                '../envmaps/fishermans_bastion/negz.jpg'
            ], false)
        });

        viewer.LoadModelFromUrlList([
            'Amazing_2902458.obj',
            'Amazing_2902458.mtl'
        ]);
    }, []); // Empty dependency array ensures this runs only once after the initial render

    return (
        <div 
          className="online_3d_viewer"
          style={{width: '800px', height: '600px'}}
          data-model="model.obj, model.mtl"
        >
        </div>
    );
};

export default View3d;
